// load strings into memory and set up string pointers
void App_LoadStrings(void)
{
	bool		more_to_get = true;
	uint8_t		the_id;
	uint8_t		str_length;
	uint8_t		strings_found = 0;
	uint8_t*	the_buff = (uint8_t*)0xC000;	// mapped to I/O bank Address, but we will map out I/O when this needs to be accessed;

	//DEBUG_OUT(("%s %d: entered", __func__, __LINE__));

	// f256jr:
	//   until load is available, I am going to be side-loading directly into physical memory with the emulator's command line
	//   all strings will be in 1 dedicated ram bank (8k)
	//   for now, rather than read length and id (without putting them in the buffer), I'll be loading the whole file
	//   this function will be about scanning what has already been loaded, and setting up the string pointers, rather than about loading
	
	// OLD NOTE:
	// logic:
	//  - string files are set up as id->len->the string. string len is limited to 255.
	//  - we will read 2 bytes to get id and length
	//  - will then read 'length' worth of bytes into the global_string_buff
	//  - will then write a null on the end of that storage loc.
	//  - will then point the next global_string[] pointer to the starting write addr
	//  - then loop around until all strings read in.

	// map the string buffer into CPU memory space
	zp_bank_num = STRING_STORAGE_VALUE;
	io_bank_value_kernel = Memory_SwapInNewBank(BANK_IO);

	asm("SEI"); // disable interrupts in case some other process has a role here
	Sys_DisableIOBank();

	while (more_to_get && strings_found < NUM_STRINGS)
	{
		the_id = *the_buff;
		//DEBUG_OUT(("%s %d: the_id=%u, the_buff=%p", __func__, __LINE__, the_id, the_buff));
		the_buff[0] = 0;	// overwrite ID spot with a NULL, as separator between strings
		++the_buff;
		
		if (the_id >= NUM_STRINGS)
		{
			more_to_get = false;
			break;
		}

		str_length = *the_buff++;
		//DEBUG_OUT(("%s %d: the_id=%u, len=%u", __func__, __LINE__, the_id, str_length));
		
		// remember where we put this string
		global_string[the_id] = (char*)the_buff;

		// move the read buffer to end of the string just read in
		the_buff += str_length;
		++strings_found;
	}

	// write out a null at end of all strings, to terminate the last one
	++the_buff;
	the_buff[0] = 0;
	
	// prepare for end of map generation mode by re-enabling the I/O page, which unmaps the dungeon map screen from 6502 RAM space
	Sys_DisableIOBank();

	zp_bank_num = io_bank_value_kernel;
	Memory_SwapInNewBank(BANK_IO);

	asm("CLI"); // restore interrupts
}


// have screen function draw the sort triangle in the right place
void Screen_UpdateSortIcons(uint8_t the_panel_x, void* the_sort_compare_function)
{
	// LOGIC:
	//    we want to draw CH_SORT_ICON immediately to the right of the column header which is now being sorted. 
	//    the positions for the sort icons are defined by UI_PANEL_FILENAME_SORT_OFFSET, etc. 
	//    we also need to undraw whatever had been set
	//    we are passed a pointer to the current compare function. we can use that to figure out what type of sort icon to draw. 
	
	// clear old icons
	Text_SetCharAtXY(the_panel_x + UI_PANEL_FILENAME_SORT_OFFSET, UI_LEFT_PANEL_HEADER_Y, CH_SPACE);
	Text_SetCharAtXY(the_panel_x + UI_PANEL_FILETYPE_SORT_OFFSET, UI_LEFT_PANEL_HEADER_Y, CH_SPACE);
	Text_SetCharAtXY(the_panel_x + UI_PANEL_FILESIZE_SORT_OFFSET, UI_LEFT_PANEL_HEADER_Y, CH_SPACE);
	
	// set new ones
	if (the_sort_compare_function == (void*)&File_CompareName)
	{
		Text_SetCharAndColorAtXY(the_panel_x + UI_PANEL_FILENAME_SORT_OFFSET, UI_LEFT_PANEL_HEADER_Y, CH_SORT_ICON, COLOR_BRIGHT_BLUE, COLOR_BLACK);
	}
	else if (the_sort_compare_function == (void*)&File_CompareFileTypeID)
	{
		Text_SetCharAndColorAtXY(the_panel_x + UI_PANEL_FILETYPE_SORT_OFFSET, UI_LEFT_PANEL_HEADER_Y, CH_SORT_ICON, COLOR_BRIGHT_BLUE, COLOR_BLACK);
	}
	else if (the_sort_compare_function == (void*)&File_CompareSize)
	{
		Text_SetCharAndColorAtXY(the_panel_x + UI_PANEL_FILESIZE_SORT_OFFSET, UI_LEFT_PANEL_HEADER_Y, CH_SORT_ICON, COLOR_BRIGHT_BLUE, COLOR_BLACK);
	}
}


// display information about f/manager, the machine, and the MicroKernel
void Screen_ShowAboutInfo(void)
{
	// LOGIC:
	//    hardware info is in VICKY
	//    info on MicroKernel build version is *** SOMEWHERE ELSE, ASK GADGET ****
	//    maybe in future do this in a window, for now, do in comms buffer
	
	uint8_t		the_machine_id;
	char 		year[2];
	char		month[2];
	char		day[2];
	char		build[2];
	char*		buffer = (char*)0xE008;

	// show app name, version, and credit
	sprintf(global_string_buff1, General_GetString(ID_STR_APP_NAME_PLATFORM_VERSION), MAJOR_VERSION, MINOR_VERSION, UPDATE_VERSION);
	Buffer_NewMessage(global_string_buff1);
	Buffer_NewMessage(General_GetString(ID_STR_ABOUT_COPYRIGHT));

	// show machine information	
	Sys_SwapIOPage(VICKY_IO_PAGE_REGISTERS);

	the_machine_id = (R8(MACHINE_ID_REGISTER) & MACHINE_MODEL_MASK);
	
	if (the_machine_id == MACHINE_F256K)
	{
		strcpy(global_string_buff1, General_GetString(ID_STR_MACHINE_K));
	}
	else if (the_machine_id == MACHINE_F256_JR)
	{
		strcpy(global_string_buff1, General_GetString(ID_STR_MACHINE_JR));
	}
	else
	{
		strcpy(global_string_buff1, General_GetString(ID_STR_MACHINE_UNKNOWN));
	}
	
	sprintf(global_string_buff2, General_GetString(ID_STR_ABOUT_HARDWARE_DETAILS), global_string_buff1, R8(MACHINE_FPGA_NUM_HI), R8(MACHINE_FPGA_NUM_LOW), R8(MACHINE_FPGA_VER_HI), R8(MACHINE_FPGA_VER_LOW), R8(MACHINE_FPGA_SUBV_HI), R8(MACHINE_FPGA_SUBV_LOW), R8(MACHINE_PCB_MAJOR), R8(MACHINE_PCB_MINOR), R8(MACHINE_PCB_ID_0), R8(MACHINE_PCB_ID_1));

	Sys_RestoreIOPage();

	Buffer_NewMessage(global_string_buff2);

	// show MicroKernel information	
	// data is (11 bytes), starting 8 bytes into 3f.bin, which happens to be mapped in to $E000 all the time. 
	// format is MM/DD/YY, space, build #. eg: "01/02/24 21"
	//General_Strlcpy(global_string_buff1, (char*)0xE008, 12); 
	month[0] = *buffer++;
	month[1] = *buffer++;
	buffer++; // skip the slash
	day[0] = *buffer++;
	day[1] = *buffer++;
	buffer++; // skip the slash
	year[0] = *buffer++;
	year[1] = *buffer++;
	buffer++; // skip the space
	build[0] = *buffer++;
	build[1] = *buffer++;
	
	strcpy(global_string_buff2, General_GetString(ID_STR_ABOUT_MICROKERNEL));
	sprintf(global_string_buff1, global_string_buff2, 
		CH_COPYRIGHT, year[0], year[1], build[0], build[1], year[0], year[1], month[0], month[1], day[0], day[1]
	);
	
	Buffer_NewMessage(global_string_buff1);
	
	// also show current bytes free
	sprintf(global_string_buff1, General_GetString(ID_STR_N_BYTES_FREE), _heapmemavail());
	Buffer_NewMessage(global_string_buff1);
}


// clear screen, set logo palette, display logo on screen, rotate colors a bit
void Screen_ShowLogo(void)
{
	uint8_t*	color[15];	// pointers to 4 bytes that make up each of the 16 colors, but leaving out 0 (black)
	uint8_t*	temp_color;
	uint8_t*	vicky_lut_addr;
	int8_t		i;
	uint8_t		j;
	uint8_t*	logo_color;
	uint8_t		color_loops;
	bool		keep_going = true;

	// set up array of pointers to each color (4 bytes) in our stored lut
	logo_color = &logo_color_lut[0];
	
	for (i = 0; i < 15; i++)
	{
		logo_color += 4;
		color[i] = logo_color;
	}

	// load in our custom logo colors to the text color luts
	Sys_SwapIOPage(VICKY_IO_PAGE_REGISTERS);
	memcpy((uint8_t*)(TEXT_BACK_LUT), &logo_color_lut, 64);
	memcpy((uint8_t*)(TEXT_FORE_LUT), &logo_color_lut, 64);
	Sys_RestoreIOPage();
	
	// clear screen and copy in chars and attrs for the logo
	// logo is 36*45, so draw it at 22, 7
	Text_ClearScreen(COLOR_BLACK, COLOR_BLACK);
	Text_CopyMemBoxLinearBuffer((uint8_t*)&logo_chars, 22, 7, 57, 51, SCREEN_COPY_TO_SCREEN, SCREEN_FOR_TEXT_CHAR);
	Text_CopyMemBoxLinearBuffer((uint8_t*)&logo_attrs, 22, 7, 57, 51, SCREEN_COPY_TO_SCREEN, SCREEN_FOR_TEXT_ATTR);

	// show app name
	sprintf(global_string_buff1, General_GetString(ID_STR_APP_NAME_PLATFORM_VERSION), MAJOR_VERSION, MINOR_VERSION, UPDATE_VERSION);
	Text_DrawStringAtXY(24, 58, global_string_buff1, 15, COLOR_BLACK);
	Text_DrawStringAtXY(28, 59, General_GetString(ID_STR_ABOUT_COPYRIGHT), 15, COLOR_BLACK);
	
	vicky_lut_addr = (uint8_t*)(TEXT_FORE_LUT + 60); // start at highest color and go down to color 1 (color 0 already loaded)
	
	// now rotate the colors around a bit for a fun effect
	// start at 2nd highest color (highest is reserved for text, so it doesn't flash)
	color_loops = 0;
	
	while (color_loops < 9 && keep_going == true)
	{
		++color_loops;
		
		for (j = 0; j < 240; j++)
		{
			temp_color = color[0];
			
			for (i = 1; i < 14; i++)
			{
				color[i-1] = color[i];
			}
			
			color[13] = temp_color;
			
			// update actual lut
			Sys_SwapIOPage(VICKY_IO_PAGE_REGISTERS);
			
			vicky_lut_addr = (uint8_t*)(TEXT_FORE_LUT + 4); // skip over black
			
			for (i = 0; i < 14; i++)
			{
				memcpy(vicky_lut_addr, color[i], 4);
				vicky_lut_addr += 4;
			}
		
			Sys_RestoreIOPage();
	
			//General_DelayTicks(120-j/2); // accelerate speed
			General_DelayTicks(4);
			
			if (Kernal_AnyKeyEvent())
			{
				keep_going = false;
				break;
			}			
		}
	}
	
	// reset colors (this is kind of clumsy, but lazy and it works)
	Sys_AutoConfigure();
}


